import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loginUID } from './actions/uidlogin';
import { ToastContainer, toast } from 'react-toastify';
import { Box, Button, Container, TextField, Card, CardContent, Typography, Avatar, Grid } from '@mui/material';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPass] = useState('');
  const dispatch = useDispatch();
  const { currentBankUser } = useSelector(state => state.loginUIDReducer);

  function loginWithUID() {
    if (!password || (email && !email.match(/.+@.+/))) {
      return toast.warning('Fill the Details Properly', {
        position: toast.POSITION.TOP_CENTER,
        autoClose: 1800,
      });
    }
    const bankuser = { email, password };
    dispatch(loginUID(bankuser));
  }

  function logout() {
    localStorage.removeItem('currentBankUser');
    window.location.href = '/';
  }

  return (
    <Container maxWidth="sm" sx={{ mt: 5 }}>
      <ToastContainer limit={2} />
      {!currentBankUser ? (
        <Card elevation={3} sx={{ padding: 4 }}>
          <Typography variant="h5" gutterBottom textAlign="center">
            BANK-UID LOGIN
          </Typography>
          <TextField
            fullWidth
            label="Email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Password"
            type="password"
            value={password}
            onChange={(e) => setPass(e.target.value)}
            margin="normal"
          />
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={loginWithUID}
            sx={{ mt: 2 }}
          >
            Login
          </Button>
        </Card>
      ) : (
        <Card elevation={3}>
          <CardContent>
            <Box textAlign="center" mb={2}>
              <Avatar
                src="https://static.vecteezy.com/system/resources/previews/039/845/042/non_2x/male-default-avatar-profile-gray-picture-grey-photo-placeholder-gray-profile-anonymous-face-picture-illustration-isolated-on-white-background-free-vector.jpg"
                sx={{ width: 80, height: 80, margin: '0 auto' }}
              />
              <Typography variant="h6" mt={1}>
                {currentBankUser.email}
              </Typography>
            </Box>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={4}>
                <Typography variant="body1" textAlign="center">
                  <strong>{currentBankUser.bdt} BDT</strong>
                  <br />
                  Available Balance
                </Typography>
              </Grid>
              <Grid item xs={12} sm={4}>
                <Typography variant="body1" textAlign="center">
                  <strong>{currentBankUser.bankUID}</strong>
                  <br />
                  Account No.
                </Typography>
              </Grid>
            </Grid>
            <Box mt={3} textAlign="center">
              <Button variant="outlined" color="primary" onClick={() => window.location.reload()} sx={{ mr: 2 }}>
                Refresh Page
              </Button>
              <Button variant="contained" color="error" onClick={logout}>
                Log Out
              </Button>
            </Box>
          </CardContent>
        </Card>
      )}
    </Container>
  );
}

export default App;
