import './App.css';
import { useDispatch, useSelector } from 'react-redux';
import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { loginUID } from './actions/uidlogin';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPass] = useState('');

  const dispatch = useDispatch();
  const { currentBankUser } = useSelector(state => state.loginUIDReducer);

  const loginWithUID = () => {
    if (!password || (email && !email.match(/.+@.+/))) {
      return toast.warning('Fill the Details Properly', {
        position: toast.POSITION.TOP_CENTER,
        autoClose: 1800,
      });
    }
    const bankuser = { email, password };
    dispatch(loginUID(bankuser));
  };

  const logout = () => {
    localStorage.removeItem('currentBankUser');
    window.location.href = '/';
  };

  useEffect(() => {
    const bankuser = { email, password };
    const timer = setTimeout(() => dispatch(loginUID(bankuser)), 500000);
    return () => clearTimeout(timer);
  }, [email, password, dispatch]);

  return (
    <div className="App">
      <ToastContainer limit={2} />
      <header className="App-header">
        <h1>Bank UID System</h1>
      </header>
      <main>
        {!currentBankUser ? (
          <div className="login-container">
            <form className="login-form">
              <h2>Login</h2>
              <input
                type="text"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPass(e.target.value)}
                required
              />
              <button type="button" onClick={loginWithUID}>
                Login
              </button>
            </form>
          </div>
        ) : (
          <div className="profile-container">
            <div className="profile-card">
              <img
                className="profile-banner"
                src="https://firebasestorage.googleapis.com/v0/b/eateryapp-48bb7.appspot.com/o/4bdbd054b3c97790e81884ada062caa5-removebg-preview.png?alt=media&token=aafd1a91-2eda-4b63-be7b-eb38f5bd7b97"
                alt="Card banner"
              />
              <div className="profile-content">
                <img
                  className="profile-avatar"
                  src="https://static.vecteezy.com/system/resources/previews/039/845/042/non_2x/male-default-avatar-profile-gray-picture-grey-photo-placeholder-gray-profile-anonymous-face-picture-illustration-isolated-on-white-background-free-vector.jpg"
                  alt="Profile"
                />
                <h3>{currentBankUser.email}</h3>
                <div className="profile-details">
                  <p>
                    <strong>Balance:</strong> {currentBankUser.bdt} BDT
                  </p>
                  <p>
                    <strong>Account No:</strong> {currentBankUser.bankUID}
                  </p>
                </div>
                <div className="profile-actions">
                  <button onClick={() => window.location.reload()}>Refresh</button>
                  <button onClick={logout}>Logout</button>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
