import logo from './logo.svg';
import './App.css';
import { BrowserRouter, Route, Link, Switch } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux';
import React, { useState, useEffect } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { loginUID } from './actions/uidlogin';

function App() {

  const [email, setEmail] = useState('');
  const [password, setPass] = useState('');

  const dispatch = useDispatch()
  const { loadingx, successx, currentBankUser } = useSelector(state => state.loginUIDReducer)
  console.log(currentBankUser)

  function loginWithUID() {

    if (!password || (email && !email.match(/.+@.+/))) {
      return toast.warning('Fill the Details Properly',
        { position: toast.POSITION.TOP_CENTER, autoClose: 1800 })
    }
    const bankuser = { email, password }
    console.log(bankuser)

    dispatch(loginUID(bankuser))
  }

  function logout() {

    localStorage.removeItem('currentBankUser');
    window.location.href = '/'
  }
  const pp = localStorage.getItem('currentBankUser')
  console.log(pp + '       x' + currentBankUser);

  useEffect(
    () => {
      const bankuser = { email, password }
      let timer1 = setTimeout(() => dispatch(loginUID(bankuser)), 500000);

      return () => {
        clearTimeout(timer1);
      };
    });

  return (
    <div className="App">
      <ToastContainer limit={2} />

      {!(currentBankUser) ?
        (
          <div>
            <form className="login" >
              <t1>BANK-UID LOGIN</t1>
              <input type="text" placeholder="Username"
                value={email} onChange={(e) => setEmail(e.target.value)} required
              />
              <input type="password" placeholder="Password"
                value={password} onChange={(e) => setPass(e.target.value)} required
              />
              <button type="button" id='log' onClick={loginWithUID} >Login</button>
            </form>
          </div>
        ) :  ////////////////////////////////////////////////////////////

        <div className="padding wow">
          <div className="pro-img"><img id='meo' src="https://static.vecteezy.com/system/resources/previews/039/845/042/non_2x/male-default-avatar-profile-gray-picture-grey-photo-placeholder-gray-profile-anonymous-face-picture-illustration-isolated-on-white-background-free-vector.jpg" alt="user" /></div>
          <div className="col-md-8">
            <div className="card">
              <img className="card-img-top" src="https://firebasestorage.googleapis.com/v0/b/eateryapp-48bb7.appspot.com/o/4bdbd054b3c97790e81884ada062caa5-removebg-preview.png?alt=media&token=aafd1a91-2eda-4b63-be7b-eb38f5bd7b97" alt="Card image cap" />
              <div className="card-body little-profile text-center">

                <div className="row text-center m-t-20 card-content">
                  <div className="col-lg-4 col-md-4 m-t-20">
                    <small>Email</small>
                    <h3 className="m-b-0 font-light" id="textt">{currentBankUser.email}</h3>
                  </div>
                  <div className="col-lg-4 col-md-4 m-t-20">
                    <small>Available Balance</small>
                    <h3 className="m-b-0" id="textt">{currentBankUser.bdt} BDT </h3>
                  </div>
                  <div className="col-lg-4 col-md-4 m-t-20">
                    <small>Account No.</small>
                    <h3 className="m-b-0 font-light" id="textt">{currentBankUser.bankUID}</h3>
                  </div>
                </div>

                {/* Padding between fields and buttons */}
                <div className="col-12 text-center mt-4">
                  <button id="ref">Refresh Page</button><br /><br />
                  <button id="blog" onClick={logout}>LOG OUT</button>
                </div>

              </div>
            </div>
          </div>
        </div>
      }
    </div>
  );
}

export default App;
