import React, { useState } from 'react'
import StripeCheckout from 'react-stripe-checkout'
import { useDispatch, useSelector } from 'react-redux';

import { placeOrder } from '../../actions/orderActions';
import { updateBalance } from '../../actions/adminAction';

import './Checkout.css'

export default function Checkout({ subtotal, onPaymentSuccess }) {

  const dispatch = useDispatch();
  const [animationState, setAnimationState] = useState('');
  const [success, setSuccess] = useState(false);
  const [processing, setProcessing] = useState(false);

  const orderState = useSelector((state) => state.placeOrderReducer)
  const userState = useSelector(state => state.loginUserReducer)

  const { currentUser } = userState
  const { loading, error, success: orderSuccess } = orderState;

  const handleSuccess = async (data) => {
    setSuccess(true);
    setProcessing(false);

    // Clear the cart after successful payment
    if (onPaymentSuccess) {
      onPaymentSuccess();
    }
  };

  function tokenHandler(token) {
    console.log(token);
    setAnimationState('loading'); // Set loading animation
    setProcessing(true);
    dispatch(placeOrder(token, subtotal));
    dispatch(updateBalance(currentUser.email, subtotal, token));
    setAnimationState('success'); // Set success animation after dispatch
    console.log("EFFECT ", loading);
  }

  return (
    <div>
      {animationState === 'loading' && (<div className="loading-animation"></div>)}
      {animationState === 'success' && (
        <div className="success-container">
          <div className="success-animation">
            <div className="success-checkmark">
              <div className="check-icon">
                <span className="icon-line line-tip"></span>
                <span className="icon-line line-long"></span>
                <div className="icon-circle"></div>
                <div className="icon-fix"></div>
              </div>
            </div>
            <div className="success-text">Payment Successful!</div>
          </div>
        </div>
      )}
      <div>
        {console.log(" return ", loading)}
        {!orderSuccess && !loading &&
          <StripeCheckout
            amount={subtotal * 100}
            shippingAddress
            token={tokenHandler}
            currency='BDT'
            stripeKey='pk_test_51LJdoPD9PVEyJI4UvvDlPGKKTlwUQOYffUqygRZU8snRITH4WQoCGQwsZWEdubhMNfxplKJAlBN4Mdg6BfBMzk0g00ADD0ottW'
            onToken={(token) => tokenHandler(token)}
            onPaymentSuccess={handleSuccess}
          >
          </StripeCheckout>
        }
        {/* <button className='btn_checkout'>Pay Now</button> */}
      </div>
    </div>
  )
}
