import React, { useState, useEffect } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useDispatch, useSelector } from 'react-redux';
import { loginUser } from '../../actions/userAction';
import { checkLoggedAsAdmin } from '../Homescreen';
import './UserLogin.css'

export default function UserLogin() {
  const [email, setEmail] = useState('');
  const [password, setPass] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const dispatch = useDispatch()
  const delay = ms => new Promise(res => setTimeout(res, ms));

  useEffect(() => {
    checkLoggedAsAdmin();
    if (localStorage.getItem('currentUser')) {
      setTimeout(this, 500)
      notify('', "Fill Up Every Field Correctly", 400)
      window.location.href = '/'
    }
  }, [])

  const { loadingx, successx } = useSelector(state => state.loginUserReducer);

  const notify = (callId, msg, timex) => {
    if (callId === '' || callId === 'passNotMatch') {
      return toast.warning(msg, { position: toast.POSITION.TOP_CENTER, autoClose: timex })
    }
    if (callId === 'reg') {
      toast.success(msg, { position: toast.POSITION.TOP_CENTER, autoClose: timex })
    }
  }

  async function loginWithUser(e) {
    e.preventDefault();
    setIsLoading(true);

    if (!email || !password || !email.match(/.+@.+/)) {
      if (!email.match(/.+@.+/)) { setEmail('') }
      setIsLoading(false);
      return notify('', "Please fill in all fields correctly", 1000)
    }

    const user = { email, password }
    dispatch(loginUser(user))
    setIsLoading(false);
  }

  return (
    <div className="login-container">
      <ToastContainer limit={2} />

      <div className="login-box">
        <div className="login-header">
          <h1>Welcome Back</h1>
          <p>Please sign in to continue</p>
        </div>

        <form className="login-form" onSubmit={loginWithUser}>
          {loadingx && 
            <div className="loading-overlay">
              <div className="loading-spinner"></div>
            </div>
          }

          {successx && !loadingx && notify('reg', "Login Successful", 2000)}

          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <div className="input-group">
              <i className="fas fa-envelope"></i>
              <input
                type="email"
                id="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <div className="input-group">
              <i className="fas fa-lock"></i>
              <input
                type="password"
                id="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPass(e.target.value)}
                required
              />
            </div>
          </div>

          <button 
            type="submit"
            className="login-button"
            disabled={isLoading}
          >
            {isLoading ? 'Signing in...' : 'Sign In'}
          </button>

          <div className="form-footer">
            <p>
              Don't have an account? 
              <a href="/register" className="register-link">Create Account</a>
            </p>
          </div>
        </form>
      </div>
    </div>
  )
}
