import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllProducts } from '../actions/productActions';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import '../App.css';

function Home() {
  const dispatch = useDispatch();
  const productstate = useSelector(state => state.getAllProductsReducer);
  const { products, error, loading } = productstate;

  useEffect(() => {
    dispatch(getAllProducts());
  }, [dispatch]);

  return (
    <div className="container mt-4">
      <h2 className="dashboard-title">
        <i className="fas fa-store"></i>
        Product Dashboard
      </h2>

      {loading ? (
        <div className="loading-state">
          <i className="fas fa-circle-notch fa-spin"></i>
          <span style={{ marginLeft: '1rem' }}>Loading products...</span>
        </div>
      ) : error ? (
        <div className="error-state">
          <i className="fas fa-exclamation-circle"></i>
          <span style={{ marginLeft: '1rem' }}>{error}</span>
        </div>
      ) : (
        <div className="row">
          {products && products.map(product => (
            <div className="col-md-4 mb-4" key={product._id}>
              <Card className="h-100 product-card">
                <Card.Img 
                  variant="top" 
                  src={product.image} 
                  className="product-image"
                />
                <Card.Body>
                  <Card.Title>{product.name}</Card.Title>
                  <Card.Text>
                    <div className="product-info">
                      <span className="product-price">à§³ {product.prices[0][product.varients[0]]}</span>
                      <span className="product-variant">{product.varients[0]}</span>
                    </div>
                    <p className="product-description">{product.description}</p>
                  </Card.Text>
                  <Link to={`/product/${product._id}`}>
                    <Button variant="primary" className="view-details-btn">
                      <i className="fas fa-info-circle"></i>
                      View Details
                    </Button>
                  </Link>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Home;
